BYTE_LENGTH = 3
BYTE_COUNTER = 3


def convert(string_list):
    next_three = 0
    counter = 0
    list = []
    final_list = []
    element = ""
    for x in string_list:
        if x == '\\':
            next_three = 1
            counter = BYTE_LENGTH
        elif x != '\\' and bool(next_three) and counter > 0:
            print(x, end='')
            element = element + x
            counter = counter -1
            if counter <= 0:
                print(" ", end='')
                next_three = 0
                list.append(int(element, 8))
                element = ""
        else:
            list.append(ord(x))
            print(ord(x), end=' ')

    print('\n')
    list_counter = BYTE_COUNTER
    triple = []
    for i in list:
        triple.append(i)
        list_counter = list_counter - 1
        if list_counter == 0:
             final_list.append(triple)
             list_counter = BYTE_COUNTER
             triple = []
    #print(final_list)
    return final_list

def convert_to_hex(convertlist):
    newlist = []
    for pixel in convertlist:
        value = "0x"
        for x in pixel:
            hexstring = hex(x)[2:]
            #print("this is hexstring:" + hexstring.__sizeof__().__str__())
            if hexstring.__sizeof__() < 27:
                hexstring = "0" + hexstring
            value = value + hexstring

        newlist.append(value)
    return newlist

def write_to_file(converted_list_bin):
    with open('code.txt', 'w') as f:
        counter = 0
        endcounter = 511
        for x in converted_list_bin:
            if endcounter == 0:
                f.write(str(x) + '')
            else:
                f.write(str(x) + ', ')
            counter = counter +1
            endcounter = endcounter -1
            if counter > 31:
                f.write("\n")
                counter = 0

def to16bit(biglist):
    newlist = []
    for color in biglist:
        newcolor = []
        counter = 0
        for x in color:
            value = x
            if(counter % 3) == 0:
                value = int(x * 0.121569)
                #print(value)
                #print(x)
            elif (counter % 3) == 1:
                value = int(x * 0.248)
            else:
                value = int(x * 0.121569)
            newcolor.append(value)
            counter = counter + 1
        newlist.append(addbinary(newcolor))
    return newlist


def addbinary(binaryelement):
    h1 = int(binaryelement[0])
    h2 = int(binaryelement[1])
    h3 = int(binaryelement[2])

    sumbin = (h1 << 11) + (h2 << 5) + h3
    return sumbin



